CREATE DEFINER=`root`@`localhost` PROCEDURE `get_asanas`(in criteria varchar(100))
BEGIN
	if criteria = 'Все' then
		#Выбор всех асан
		select
			asana
		from asanas
		order by sequence_number;
	else
		# Определение количества асан в зависимости от задействованной мышцы
		select
			count(asana)
		from involved_muscles
		where muscle = criteria
		into @count_asana;
        
        # Определение того, как формировать асаны
        if @count_asana > 0 then
			#Выбор асан в зависимости от задействованной мышцы
			select
				a.asana
			from asanas as a
            join involved_muscles as i_m on a.asana = i_m.asana
			where i_m.muscle = criteria
			order by sequence_number;	
        else
			#Выбор асан в зависимости от комплекса или специализации
			select
				a.asana
			from asanas as a
			join asanas_specializations as a_s on a.asana = a_s.asana
			join specializations as s on a_s.specialization = s.specialization
			where s.complex = criteria or a_s.specialization = criteria
			group by a.asana
			order by sequence_number;
		end if;
	end if;
END ;;
DELIMITER ;
