CREATE DEFINER=`root`@`localhost` PROCEDURE `get_asanas_on_the_sides_quantity`()
BEGIN
	select
		t1.asana,
		t1.side,
		count(*) as quantity
	from
		(select
			asana,
			side,
			sum(time_work) as sum_time_work
		from data_yoga
		where side in ("О", "Л", "П")
		group by asana, side, date_yoga
		having sum_time_work > 0) as t1
	group by asana, side
	order by asana;
END ;;
