CREATE DEFINER=`root`@`localhost` PROCEDURE `get_exercises`(IN criteria VARCHAR(100))
BEGIN
	IF criteria = 'Все' THEN
		#Выбор всех упражнений
		SELECT
			exercise
		FROM exercises
		ORDER BY exercise;
	ELSE 
		#Выбор упражнений в зависимости от мышечной группы или мышцы
		SELECT
			exercise
		FROM exercises
		JOIN muscles ON exercises.muscle = muscles.muscle
		where muscles.muscle_group = criteria OR muscles.muscle = criteria
		ORDER BY exercise;
	END IF;
END ;;
