CREATE DEFINER=`root`@`localhost` PROCEDURE `get_statistics`(in criteria varchar(50))
BEGIN
	case criteria
		# Тренировка
		# Количество выполнений:
        when 'muscle_groups_quantity' then call get_muscle_groups_quantity();
        when 'muscles_quantity' then call get_muscles_quantity();
        when 'exercises_on_the_sides_quantity' then call get_exercises_on_the_sides_quantity();
        # КПШ и тренировочный объем за:
        when 'for_workout_number' then call get_for_workout_number();
        when 'per_day_number' then call get_per_day_number();
        # Последний день тренировки:
        when 'last_training_muscle_group' then call get_last_training_muscle_group();
        when 'last_training_muscle' then call get_last_training_muscle();
        when 'last_training_exercise' then call get_last_training_exercise();
		# Йога
		# Количество выполнений:
        when 'complexes_quantity' then call get_complexes_quantity();
        when 'specializations_quantity' then call get_specializations_quantity();
        when 'asanas_on_the_sides_quantity' then call get_asanas_on_the_sides_quantity();
        # Сумма циклов вдох-выдох за:
        when 'for_yoga_number' then call get_for_yoga_number();
        when 'per_practice_number' then call get_per_practice_number();
        # Последний день практики:
        when 'last_training_complex' then call get_last_training_complex();
        when 'last_training_specialization' then call get_last_training_specialization();
        # Растяжка
		# Количество выполнений:
        when 's_asanas_on_the_sides_quantity' then call get_s_asanas_on_the_sides_quantity();
        # Последний день практики:
        when 'last_training_asana' then call get_last_training_asana();
    end case;	
END ;;
