CREATE DEFINER=`root`@`localhost` PROCEDURE `get_training_schedule`()
BEGIN
	drop temporary table if exists training_schedule;

	# Создание временной таблицы "Расписание занятий"
	create temporary table training_schedule
    # Выборка данных йоги
	select
		"Йога" as training_name,
		week_№,
		day_№,
		complex as unknown_1,
		specialization as unknown_2,
		date_yoga as date_training
	from data_yoga
	group by week_№, day_№, complex, specialization, date_yoga
	union
    # Выборка данных тренировки
	select
		"Тренировка" as training_name,
		week_№,
		day_№,
		mesocycle,
		microcycle,
		date_workout
	from data_workout
	group by week_№, day_№, mesocycle, microcycle, date_workout
	union
    # Выборка данных растяжки
	select
		"Растяжка" as training_name,
		week_№,
		day_№,
		"Пусто" as unknown_1,
		"Пусто" as unknown_2,
		date_stretching
	from data_stretching
	group by week_№, day_№, date_stretching;
    
    # Выборка данных из временной таблицы "Расписание занятий"
	select * from training_schedule
	order by date_training desc;  
END ;;
