CREATE DEFINER=`root`@`localhost` PROCEDURE `update_data_stretching`(
	in pose_number tinyint, 
    in asana_name varchar(70), 
	in stretching_date date, 
    in index_changeable_column tinyint, 
	in new_side_or_note varchar(5000), 
	in new_time_work_or_rest tinyint
)
BEGIN
	case index_changeable_column
		when 4 then 	
			update data_stretching
			set side = new_side_or_note
			where №_pose = pose_number and asana = asana_name and date_stretching = stretching_date
			limit 1;
        when 5 then 	
			update data_stretching
			set time_work = new_time_work_or_rest
			where №_pose = pose_number and asana = asana_name and date_stretching = stretching_date
			limit 1;
        when 6 then 
			update data_stretching
			set time_rest = new_time_work_or_rest
			where №_pose = pose_number and asana = asana_name and date_stretching = stretching_date
			limit 1;
        when 7 then
			update data_stretching
			set note = new_side_or_note
			where №_pose = pose_number and asana = asana_name and date_stretching = stretching_date
			limit 1;
    end case;
END ;;
